import { Component, EventEmitter, OnInit, Output } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, NgForm, Validators } from '@angular/forms';
import { ActivatedRoute, ParamMap, Router } from '@angular/router';
import { Post } from 'src/app/interfaces/posts.model';
import { HttpService } from 'src/app/services/http.service';

@Component({
  selector: 'app-post-create',
  templateUrl: './post-create.component.html',
  styleUrls: ['./post-create.component.scss']
})
export class PostCreateComponent implements OnInit {
    postId: any = "";
    post = {
      title: "",
      content: ""
    };
    mode = "create";
    isLoading = false;
    form:FormGroup;
    @Output() newPost = new EventEmitter<any>();

    constructor(private router: Router, private http: HttpService, private activatedRoute: ActivatedRoute){
      this.form = new FormGroup({
        title: new FormControl("", { validators: [Validators.required], updateOn: "blur" }),
        content: new FormControl("", { validators: [Validators.required], updateOn: "blur" })
      })
    }

    ngOnInit(): void {
      this.activatedRoute.paramMap.subscribe((params: ParamMap) => {
        if(params.get("id")){
          this.mode = "edit";
          this.post = {
            title: this.post.title,
            content: this.post.content
          };
          this.postId = params.get("id");
        }else {
          console.log("id dosent exists");
          this.mode = "create";
          this.postId = "";
        }
      })

     this.getPost();

    }

    onCreatePost(){
      if (this.form.invalid) return;

      if(this.mode === "create"){
        this.isLoading = true;
        this.http.post("posts", {title: this.form.value.title, content: this.form.value.content}, (data: any) => {
          if(data.success){
            this.isLoading = false;
          }
        });
      }else if(this.mode === "edit"){
        this.isLoading = true;
        this.http.patch(`posts/${this.postId}`, {title: this.form.value.title, content: this.form.value.content}, (data: any) => {
          if(data.success){
            console.log("sent");
            this.isLoading = false;
          }
        });
      }

      this.form.reset();
    }

    getPost(){
      this.http.get(`posts/${this.postId}`, (data: any) => {
        this.post = data.data;
      })
    }


}
<section class="container">
  <mat-card>
    <mat-spinner *ngIf="isLoading"></mat-spinner>
    <mat-card-content>
      <form [formGroup]="form" (submit)="onCreatePost()">
        <mat-form-field>
          <input
            type="text"
            placeholder="Post title"
            matInput
            name="title"
            formControlName="title"
            required
            [ngModel]="post.content"
          />
          <mat-error *ngIf="form.get()">Post Title is required</mat-error>
        </mat-form-field>

        <br>

        <div>
          <button mat-stroked-button (click)="filePicker.click()">Pick Image</button>
          <input type="file" #filePicker>
        </div>

        <br>

        <mat-form-field>
          <textarea
            placeholder="Post content"
            id="content"
            cols="30"
            rows="10"
            name="content"
            [ngModel]="post.content"
            matInput
            required
            #content="ngModel"
          ></textarea>
          <mat-error *ngIf="content?.invalid">Content is required</mat-error>
        </mat-form-field>

        <br>

        <button type="submit" mat-raised-button color="primary">Save Post</button>
      </form>
    </mat-card-content>
  </mat-card>
</section>
