import { Component, EventEmitter, Output } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Post } from 'src/app/interfaces/posts.model';

@Component({
  selector: 'app-post-create',
  templateUrl: './post-create.component.html',
  styleUrls: ['./post-create.component.scss']
})
export class PostCreateComponent {
    @Output() newPost = new EventEmitter<any>();
    post: Post = {
      _id: Math.random().toString(), 
      title: "",
      content: "",
    }

    onCreatePost(form: NgForm){
      console.log("title", this.post.title);
      console.log("title", this.post.content);
      this.newPost.emit(this.post);
      form.resetForm();
    }

}
<form class="container" #postForm="ngForm" (ngSubmit)="onCreatePost(postForm)">
  <mat-card>
    <mat-card-content>
      <form>
        <mat-form-field>
          <input
            type="text"
            placeholder="Post title"
            matInput
            name="title"
            [(ngModel)]="post.title"
          />
        </mat-form-field>

        <br>

        <mat-form-field>
          <textarea
            placeholder="Post content"
            id="content"
            cols="30"
            rows="10"
            name="content"
            [(ngModel)]="post.content"
            matInput
          ></textarea>
        </mat-form-field>

        <br>

        <button mat-raised-button color="primary">Save Post</button>
      </form>
    </mat-card-content>
  </mat-card>
</form>
