import { Component, EventEmitter, OnInit, Output } from '@angular/core';
import { NgForm } from '@angular/forms';
import { ActivatedRoute, ParamMap, Router } from '@angular/router';
import { Post } from 'src/app/interfaces/posts.model';
import { HttpService } from 'src/app/services/http.service';

@Component({
  selector: 'app-post-create',
  templateUrl: './post-create.component.html',
  styleUrls: ['./post-create.component.scss']
})
export class PostCreateComponent implements OnInit {
    postId: any = "";
    mode = "create"
    @Output() newPost = new EventEmitter<any>();

    constructor(private router: Router, private http: HttpService, private activatedRoute: ActivatedRoute){}

    ngOnInit(): void {
      console.log("activatedRoute", this.activatedRoute);
      this.activatedRoute.paramMap.subscribe((params: ParamMap) => {
        this.postId = params.get("id");
        console.log("this.postId", this.postId);
      })
    }

    onCreatePost(form: NgForm){
      if (form.invalid) return;
      this.http.post("posts", {title: form.value.title, content: form.value.content}, (data: any) => {
        if(data.success){
          console.log("sent");
        }
      });
      form.resetForm();
      // this.router.navigate(["/"]);
    }


}
<section class="container">
  <mat-card>
    <mat-card-content>
      <form #postForm="ngForm" (submit)="onCreatePost(postForm)">
        <mat-form-field>
          <input
            type="text"
            placeholder="Post title"
            matInput
            name="title"
            ngModel
            required
            #title="ngModel"
          />
          <mat-error *ngIf="title?.invalid">Post Title is required</mat-error>
        </mat-form-field>

        <br>

        <mat-form-field>
          <textarea
            placeholder="Post content"
            id="content"
            cols="30"
            rows="10"
            name="content"
            ngModel
            matInput
            required
            #content="ngModel"
          ></textarea>
          <mat-error *ngIf="content?.invalid">Content is required</mat-error>
        </mat-form-field>

        <br>

        <button type="submit" mat-raised-button color="primary">Save Post</button>
      </form>
    </mat-card-content>
  </mat-card>
</section>
