import { Component, EventEmitter, Output } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Router } from '@angular/router';
import { Post } from 'src/app/interfaces/posts.model';

@Component({
  selector: 'app-post-create',
  templateUrl: './post-create.component.html',
  styleUrls: ['./post-create.component.scss']
})
export class PostCreateComponent {
    @Output() newPost = new EventEmitter<any>();
    post: Post = {
      _id: Math.random().toString(), 
      title: "",
      content: "",
    }

    constructor(private router: Router){}

    onCreatePost(form: NgForm){
      this.newPost.emit(this.post);
      form.resetForm();
      // this.router.navigate(["/"]);
    }


}
<section class="container">
  <mat-card>
    <mat-card-content>
      <form #postForm="ngForm" (ngSubmit)="onCreatePost(postForm)">
        <mat-form-field>
          <input
            type="text"
            placeholder="Post title"
            matInput
            name="title"
            ngModel
            required
          />
        </mat-form-field>

        <br>

        <mat-form-field>
          <textarea
            placeholder="Post content"
            id="content"
            cols="30"
            rows="10"
            name="content"
            ngModel
            matInput
            required
          ></textarea>
        </mat-form-field>

        <br>

        <button type="submit" mat-raised-button color="primary">Save Post</button>
      </form>
    </mat-card-content>
  </mat-card>
</section>
