import { Component, EventEmitter, Output } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Router } from '@angular/router';
import { Post } from 'src/app/interfaces/posts.model';

@Component({
  selector: 'app-post-create',
  templateUrl: './post-create.component.html',
  styleUrls: ['./post-create.component.scss']
})
export class PostCreateComponent {
    @Output() newPost = new EventEmitter<any>();

    constructor(private router: Router){}

    onCreatePost(form: NgForm){
      if (form.invalid) return;
      console.log({ _id: Math.random().toString(), title: form.value.title, content: form.value.content  });
      form.resetForm();
      // this.router.navigate(["/"]);
    }


}
<section class="container">
  <mat-card>
    <mat-card-content>
      <form #postForm="ngForm" (submit)="onCreatePost(postForm)">
        <mat-form-field>
          <input
            type="text"
            placeholder="Post title"
            matInput
            name="title"
            ngModel
            required
            #title="ngModel"
          />
          <mat-error *ngIf="title.invalid">Post Title is required</mat-error>
        </mat-form-field>

        <br>

        <mat-form-field>
          <textarea
            placeholder="Post content"
            id="content"
            cols="30"
            rows="10"
            name="content"
            ngModel
            matInput
            required
          ></textarea>
        </mat-form-field>

        <br>

        <button type="submit" mat-raised-button color="primary">Save Post</button>
      </form>
    </mat-card-content>
  </mat-card>
</section>
