import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { Post } from 'src/app/interfaces/posts.model';
import { HttpService } from 'src/app/services/http.service';

@Component({
  selector: 'app-posts-list',
  templateUrl: './posts-list.component.html',
  styleUrls: ['./posts-list.component.scss']
})
export class PostsListComponent implements OnInit {
  panelOpenState = false;

  posts: Post[] = [];
  @Output() postDeleted = new EventEmitter();

  constructor(private http: HttpService, private router){}

  ngOnInit(): void {
    this.getPosts();  
  }

  getPosts(){
    this.http.get("posts", (data: any) => {
      if(data.success){
        this.posts = data.data;
      }
    })
  }

  deletePost(id: any){
    this.http.delete(`posts/${id}`, (data: any) => {
      if(data.success){
        this.getPosts();
      }
    })
  }

  editPost(post: any){
    this.http.patch(`posts/${post._id}`, post, (data: any) => {
      if(data.success){
        this.getPosts();
      }
    });
  }



}
