import { Component, EventEmitter, OnInit, Output } from '@angular/core';
import { NgForm } from '@angular/forms';
import { ActivatedRoute, ParamMap, Router } from '@angular/router';
import { Post } from 'src/app/interfaces/posts.model';
import { HttpService } from 'src/app/services/http.service';

@Component({
  selector: 'app-post-create',
  templateUrl: './post-create.component.html',
  styleUrls: ['./post-create.component.scss']
})
export class PostCreateComponent implements OnInit {
    postId: any = "";
    post = {
      title: "",
      content: ""
    };
    mode = "create";
    @Output() newPost = new EventEmitter<any>();

    constructor(private router: Router, private http: HttpService, private activatedRoute: ActivatedRoute){}

    ngOnInit(): void {
      this.activatedRoute.paramMap.subscribe((params: ParamMap) => {
        if(params.get("id")){
          this.mode = "edit";
          this.post = {
            title: this.post.title,
            content: this.post.content
          };
          this.postId = params.get("id");
        }else {
          console.log("id dosent exists");
          this.mode = "create";
          this.postId = "";
        }
      })

     this.getPost();

    }

    onCreatePost(form: NgForm){
      if (form.invalid) return;

      if(this.mode === "create"){
        this.http.post("posts", {title: form.value.title, content: form.value.content}, (data: any) => {
          if(data.success){
            console.log("sent");
          }
        });
      }else if(this.mode === "edit"){
        this.http.patch(`posts/${this.postId}`, {title: form.value.title, content: form.value.content}, (data: any) => {
          if(data.success){
            console.log("sent");
          }
        });
      }

      form.resetForm();
    }

    getPost(){
      this.http.get(`posts/${this.postId}`, (data: any) => {
        this.post = data.data;
      })
    }


}
